-- ----------------------------
-- Table structure for jaguar_codegen_code_template
-- ----------------------------
DROP TABLE IF EXISTS `jaguar_codegen_code_template`;
CREATE TABLE `jaguar_codegen_code_template`
(
    `id_`                   bigint(20) UNSIGNED                                   NOT NULL COMMENT 'ID',
    `code_template_name`    varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '模板类型',
    `code_template_file`    text CHARACTER SET utf8mb4 COLLATE utf8mb4_bin        NOT NULL COMMENT '模板文件',
    `code_template_version` int(11)                                               NOT NULL COMMENT '模板版本',
    `file_name`             varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '文件名',
    `file_path`             varchar(45) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NOT NULL COMMENT '文件路径',
    `deleted_`              tinyint(1)                                            NULL DEFAULT 0 COMMENT '逻辑删除标记(0--正常 1--删除)',
    `create_time`           timestamp(0)                                          NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '创建时间',
    `update_time`           timestamp(0)                                          NULL DEFAULT CURRENT_TIMESTAMP(0) COMMENT '更新时间',
    `remark_`               varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_bin NULL DEFAULT NULL COMMENT '备注',
    PRIMARY KEY (`id_`) USING BTREE
) ENGINE = InnoDB
  CHARACTER SET = utf8mb4
  COLLATE = utf8mb4_bin COMMENT = '代码模板'
  ROW_FORMAT = Dynamic;

