        'import request from \'@/axios/index\'\n\nconst baseurl = \'/${module}/${entityName}\'\n\nexport function fetchList(query) {\n  for (const key in query) {\n    if (query[key] == null || query[key] === \'\') {\n      delete query[key]\n    }\n  }\n  return request({\n    url: baseurl + \'/page\',\n    method: \'get\',\n    params: query,\n  })\n}\n\nexport function getDetail(id) {\n  return request({\n    url: baseurl + \'/\' + id,\n    method: \'get\'\n  })\n}\n\nexport function save(data) {\n  return request({\n    url: baseurl,\n    method: \'post\',\n    data,\n  })\n}\n\nexport function update(data) {\n  return request({\n    url: baseurl,\n    method: \'put\',\n    data,\n  })\n}\n\nexport function del(id) {\n  return request({\n    url: baseurl + \'/\' + id,\n    method: \'delete\',\n  })\n}\n',
