INSERT INTO `jaguar_codegen_code_template`
VALUES (1435876335762817025, 'table.vue',
        '<template>\n  <div>\n    <div class=\"jaguar-search\">\n      <el-form :inline=\"true\" :model=\"searchForm\" @submit.prevent>\n#foreach($column in ${columns})\n        <search-input\n          label=\"${column.columnComment}\"\n          v-model=\"searchForm.${column.fieldName}\"\n          :search=\"getData\"\n        />\n#end\n        <el-form-item>\n          <search-button :search=\"getData\" />\n          <reset-button :search-form=\"searchForm\" :search=\"getData\" />\n        </el-form-item>\n      </el-form>\n    </div>\n\n    <div class=\"jaguar-control\">\n      <add-button @click=\"handleCreate\" />\n    </div>\n\n    <div class=\"jaguar-table\">\n      <el-table :data=\"tableData\">\n#foreach($column in ${columns})\n        <el-table-column prop=\"${column.fieldName}\" label=\"${column.columnComment}\" />\n#end\n        <el-table-column label=\"操作\">\n          <template #default=\"scope\">\n            <view-button @click=\"handleView(scope.row)\" />\n            <edit-button @click=\"handleEdit(scope.row)\" />\n            <delete-button @click=\"handleDelete(scope.row)\" />\n          </template>\n        </el-table-column>\n      </el-table>\n\n      <table-page :page=\"page\" :search=\"getData\" />\n    </div>\n\n    <Edit :dialog=\"dialog\" @success=\"getData()\" />\n  </div>\n</template>\n<script>\nimport { fetchList, del } from \'@/api/${module}/${entityName}\'\nimport Edit from \'./Edit\'\n\nexport default {\n  name: \'${entity}\',\n  data() {\n    return {\n      tableData: [],\n      searchForm: {},\n      page: {\n        total: 0,\n        current: 1,\n        size: 10,\n      },\n      dialog: {\n        id: null,\n        operation: null,\n        visible: false,\n      },\n    }\n  },\n  components: {\n    Edit,\n  },\n  created() {\n    this.getData()\n  },\n  methods: {\n    getData(current, size) {\n      if (current) this.page.current = current\n      if (size) this.page.size = size\n\n      fetchList(\n        Object.assign(\n          {\n            descs: \'update_time\',\n            current: this.page.current,\n            size: this.page.size,\n          },\n          this.searchForm\n        )\n      ).then((res) => {\n        this.tableData = res.data.data.records\n        this.page.total = res.data.data.total\n      })\n    },\n    handleCreate() {\n      this.dialog.entity = null\n      this.dialog.operation = 1\n      this.dialog.visible = true\n    },\n    handleEdit(row) {\n      this.dialog.entity = row\n      this.dialog.operation = 2\n      this.dialog.visible = true\n    },\n    handleView(row) {\n      this.dialog.entity = row\n      this.dialog.operation = 3\n      this.dialog.visible = true\n    },\n    handleDelete(row) {\n      this.$confirm(\'是否确认删除该数据\', \'提示\', {\n        type: \'warning\',\n        closeOnClickModal: false,\n      })\n        .then(() => {\n          del(row.id).then(() => {\n            this.$message.success(\'删除成功\')\n            this.getData()\n          })\n        })\n        .catch(console.debug)\n    },\n  },\n}\n</script>',
        2, 'index.vue', 'src/views/${module}/${entityName}', 0, '2021-06-21 10:10:43', '2021-06-21 10:10:43', NULL);
