() {\n      #[[this.$refs.dataForm.validate((valid) => {\n        if (valid) {\n          let edit = this.operation == 1 ? save : update\n          edit(this.entity).then(() => {\n            this.dialog.visible = false\n            this.$message.success(\'保存成功\')\n            this.$emit(\'success\')\n          })\n        }\n      })]]#\n    },\n  },\n}\n</script>',
